mapper.select:
      SELECT b.* FROM board b
	  ORDER BY b.ref DESC, b.re_step ASC
	  LIMIT #{startRow}, #{blockCount}

mapper.update:
UPDATE board
SET re_step = re_step +1
WHERE ref=#{ref} AND re_step > #{re_step}

UPDATE board
SET readCount = readCount + 1
WHERE num=#{num}

UPDATE board
SET subject=#{subject}, content=#{content}

   ,upload=#{upload}

WHERE num=#{num}

mapper.select:
SELECT upload
FROM board
WHERE num=#{num}
